SET NAMES utf8;
SET time_zone = '+00:00';
SET foreign_key_checks = 0;
SET sql_mode = 'NO_AUTO_VALUE_ON_ZERO';

DROP TABLE IF EXISTS `admin`;
CREATE TABLE `admin` (
  `admin_id` int(55) NOT NULL AUTO_INCREMENT,
  `admin_name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `admin_surname` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `admin_email` varchar(55) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `admin_password` varchar(55) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `admin_group` tinyint(2) NOT NULL,
  PRIMARY KEY (`admin_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


DROP TABLE IF EXISTS `bibliography`;
CREATE TABLE `bibliography` (
  `bibliography_id` smallint(15) NOT NULL AUTO_INCREMENT,
  `bibliography_title` varchar(55) NOT NULL,
  PRIMARY KEY (`bibliography_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


DROP TABLE IF EXISTS `bibliography_item`;
CREATE TABLE `bibliography_item` (
  `bibliography_item_id` int(55) NOT NULL AUTO_INCREMENT,
  `bibliography_id` smallint(15) NOT NULL,
  `category_id` tinyint(3) NOT NULL,
  `upload_file_id` int(255) NOT NULL,
  `bibliography_item_title` varchar(255) NOT NULL,
  `bibliography_item_content` text NOT NULL,
  `bibliography_editor` varchar(255) NOT NULL,
  `bibliography_release` int(25) NOT NULL,
  `bibliography_item_rank` smallint(15) NOT NULL,
  PRIMARY KEY (`bibliography_item_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


DROP TABLE IF EXISTS `books`;
CREATE TABLE `books` (
  `book_id` int(55) NOT NULL AUTO_INCREMENT,
  `upload_file_id` int(55) NOT NULL,
  `book_title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `book_author` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `book_editor` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `book_detail_1` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `book_detail_2` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `place` varchar(115) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `original_edition_date` varchar(25) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `re_edition_date` varchar(25) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `no_of_pages` varchar(25) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `book_category` tinyint(2) NOT NULL,
  `book_description` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  PRIMARY KEY (`book_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


DROP TABLE IF EXISTS `category`;
CREATE TABLE `category` (
  `category_id` mediumint(3) NOT NULL AUTO_INCREMENT,
  `category_parent_id` mediumint(3) NOT NULL,
  `category_name` varchar(55) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  PRIMARY KEY (`category_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


DROP TABLE IF EXISTS `chronology`;
CREATE TABLE `chronology` (
  `chronology_id` int(55) NOT NULL AUTO_INCREMENT,
  `chronology_year` int(6) NOT NULL,
  `chronology_description` text NOT NULL,
  `book_id_1` int(55) NOT NULL,
  `book_id_2` int(55) NOT NULL,
  `book_id_3` int(5) NOT NULL,
  PRIMARY KEY (`chronology_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


DROP TABLE IF EXISTS `language`;
CREATE TABLE `language` (
  `language_id` smallint(3) NOT NULL,
  `language_name` varchar(55) NOT NULL,
  `language_short_name` varchar(25) NOT NULL,
  PRIMARY KEY (`language_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


DROP TABLE IF EXISTS `news`;
CREATE TABLE `news` (
  `news_id` int(255) NOT NULL AUTO_INCREMENT,
  `news_category_id` int(255) NOT NULL,
  `subcategory_id` int(55) NOT NULL,
  `news_time` time NOT NULL,
  `language_id` smallint(3) NOT NULL,
  `news_title` varchar(55) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `news_title_2` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `news_title_3` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `news_subtitle` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `news_short_title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `pool_list_2` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `news_content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `upload_file_id` int(55) NOT NULL,
  `dir_name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `sound_dir_name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `bio_dir_name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `news_public_date` varchar(55) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `admin_id` tinyint(3) NOT NULL,
  `news_status` tinyint(1) NOT NULL DEFAULT 1,
  `news_rank` tinyint(3) NOT NULL DEFAULT 1,
  `news_publication_start_date` date NOT NULL,
  `news_publication_end_date` date NOT NULL,
  `news_creation_date` date NOT NULL,
  `news_modification_date` date NOT NULL,
  PRIMARY KEY (`news_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


DROP TABLE IF EXISTS `news_category`;
CREATE TABLE `news_category` (
  `news_category_id` int(55) NOT NULL AUTO_INCREMENT,
  `news_category_parent_id` smallint(3) NOT NULL,
  `news_category_name` varchar(255) NOT NULL,
  PRIMARY KEY (`news_category_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


DROP TABLE IF EXISTS `pages`;
CREATE TABLE `pages` (
  `page_id` mediumint(55) NOT NULL AUTO_INCREMENT,
  `profile_id` mediumint(55) NOT NULL,
  `page_type` tinyint(3) NOT NULL,
  `page_title` varchar(255) NOT NULL,
  `page_content` text NOT NULL,
  `dir_name` varchar(55) NOT NULL,
  `media_url` varchar(55) NOT NULL,
  `discography_id` int(55) NOT NULL,
  `page_rank` tinyint(2) NOT NULL DEFAULT 1,
  PRIMARY KEY (`page_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


DROP TABLE IF EXISTS `profile`;
CREATE TABLE `profile` (
  `profile_id` int(11) NOT NULL AUTO_INCREMENT,
  `dir_name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '0',
  `dir_name_2` varchar(255) NOT NULL,
  `media_url` varchar(255) NOT NULL,
  `profile_category_id` smallint(15) NOT NULL,
  `upload_file_id` int(255) NOT NULL,
  `profile_name` varchar(55) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `profile_surname` varchar(55) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `profile_short_biography` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `profile_function` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `profile_company` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `profile_company_url` varchar(55) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `profile_email_address` varchar(55) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `profile_phone_number` varchar(55) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `profile_address` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `profile_city` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `profile_postal_code` varchar(55) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `profile_country` smallint(12) NOT NULL,
  `profile_status` tinyint(2) NOT NULL DEFAULT 1,
  `profile_creation` date NOT NULL,
  `profile_modification` date NOT NULL DEFAULT '0000-00-00',
  PRIMARY KEY (`profile_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


DROP TABLE IF EXISTS `profile_category`;
CREATE TABLE `profile_category` (
  `profile_category_id` smallint(15) NOT NULL AUTO_INCREMENT,
  `profile_category_name_en` varchar(255) NOT NULL,
  `profile_category_name_fr` varchar(255) NOT NULL,
  PRIMARY KEY (`profile_category_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


DROP TABLE IF EXISTS `profile_description`;
CREATE TABLE `profile_description` (
  `profile_description_id` int(255) NOT NULL AUTO_INCREMENT,
  `profile_id` int(255) NOT NULL,
  `language_id` smallint(10) NOT NULL DEFAULT 1,
  `profile_description_content` text NOT NULL,
  PRIMARY KEY (`profile_description_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


DROP TABLE IF EXISTS `profile_picture`;
CREATE TABLE `profile_picture` (
  `profile_picture_id` int(55) NOT NULL AUTO_INCREMENT,
  `profile_id` int(55) NOT NULL,
  `profile_picture_name` varchar(255) NOT NULL,
  `profile_picture_ext` varchar(5) NOT NULL,
  `creation_date` datetime NOT NULL,
  PRIMARY KEY (`profile_picture_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


DROP TABLE IF EXISTS `subcategory`;
CREATE TABLE `subcategory` (
  `subcategory_id` smallint(3) NOT NULL AUTO_INCREMENT,
  `subcategory_parent_id` smallint(3) NOT NULL,
  `subcategory_name` varchar(55) NOT NULL,
  PRIMARY KEY (`subcategory_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


DROP TABLE IF EXISTS `template`;
CREATE TABLE `template` (
  `template_id` int(255) NOT NULL AUTO_INCREMENT,
  `template_parent_id` int(255) NOT NULL,
  `template_name` varchar(55) NOT NULL,
  PRIMARY KEY (`template_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


DROP TABLE IF EXISTS `upload_file`;
CREATE TABLE `upload_file` (
  `upload_file_id` int(55) NOT NULL AUTO_INCREMENT,
  `profile_id` int(55) NOT NULL DEFAULT 0,
  `dir_name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `upload_file_name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `upload_file_ext` varchar(5) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `language_id` tinyint(5) NOT NULL DEFAULT 1,
  `upload_file_title` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `upload_file_description` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `upload_file_rank` mediumint(25) NOT NULL DEFAULT 1,
  `upload_file_display` tinyint(1) NOT NULL DEFAULT 1,
  `upload_file_creation_date` date NOT NULL,
  `upload_file_modification_date` date NOT NULL,
  PRIMARY KEY (`upload_file_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `id` int(32) NOT NULL AUTO_INCREMENT,
  `username` varchar(32) NOT NULL,
  `first_name` varchar(64) NOT NULL,
  `last_name` varchar(64) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


DROP TABLE IF EXISTS `writing`;
CREATE TABLE `writing` (
  `writing_id` int(255) NOT NULL AUTO_INCREMENT,
  `template_id` int(255) NOT NULL,
  `language_id` smallint(3) NOT NULL,
  `writing_title` varchar(55) NOT NULL,
  `writing_description` varchar(55) NOT NULL,
  `writing_content` text NOT NULL,
  `upload_file_id` int(55) NOT NULL,
  `dir_name` varchar(255) NOT NULL,
  `media_url` varchar(255) NOT NULL,
  `writing_public_date` varchar(55) NOT NULL,
  `admin_id` tinyint(3) NOT NULL,
  `writing_status` tinyint(1) NOT NULL,
  `writing_rank` tinyint(3) NOT NULL,
  `writing_publication_start_date` date NOT NULL,
  `writing_publication_end_date` date NOT NULL,
  `writing_creation_date` date NOT NULL,
  `writing_modification_date` date NOT NULL,
  PRIMARY KEY (`writing_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


